<!--课程管理列表-->
<template>
  <div>
    <el-table
      :data="baseData.hot"
      row-key="id"
      min-width="772px"
      tooltip-effect="light"
    >
      <el-table-column type="index" min-width="100px" label="排名" prop="rankImg">
        <template #default="scope">
          <p style="display:flex">
          <img :src="scope.row.rankImg" alt="" style="width: 24px;height:24px"/>
        </p>
        </template>
      </el-table-column>
      <el-table-column
        label="课程分类"
        min-width="220px"
        class-name="textLeft"
        prop="category"
        show-overflow-tooltip="true"
      >
      </el-table-column>
      <el-table-column label="课程名称" min-width="250px" prop="name"  show-overflow-tooltip="true">
      </el-table-column>
      <el-table-column
        prop="newStuNum"
        label="新增学员（人）"
        min-width="122px"
        align="center"
      >
      </el-table-column>
      <el-table-column
        prop="orderAmount"
        label="课程金额（元）"
        min-width="125px"
        class-name="textLeft"
        align="center"
      >
        <template #default="scope">
          {{ (scope.row.orderAmount/100).toFixed(2) }}
        </template>
      </el-table-column>
    </el-table>
  </div>
</template>
<script setup>
import { reactive, watch } from "vue"

// 接收父级传参
const props = defineProps({
  formData: {
    type: Object,
    default: () => ({}),
  }
})
const baseData = reactive({
  hot: [{
    rankImg:'',
  }],
}) //接收父组件返回的数据
// 创建一个空对象，存储排名图片路径
const rankImg = [
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,",
    "data:image/png;base64,"
]
// 监听父组件传值，赋值
watch(props, (newVal, oldVal) => {
  getData()
})
// 监听baseData的变化，变化时，将图片数组赋值给baseData
watch(baseData, (val) => {
  addRankImg()
  // console.log(baseData)
})
// 获取数据
const getData = () => {
  // 将formData中的数据赋值给baseData.hot
  baseData.hot = props.formData
}
// 创建方法，给每个数据添加排名图片路径
const addRankImg = () => {
  baseData.hot.forEach((item, index) => {
    item.rankImg = rankImg[index]
  })
}
</script>
